// 文件：src/components/HeroIntro.jsx（覆盖）
import React from 'react';
import { motion } from 'framer-motion';
import { colors, fonts, radius, shadow } from '../theme';

const HeroIntro = () => {
  return (
    <section style={{ padding: '4rem 2rem', backgroundColor: colors.background }}>
      <motion.div
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1.2 }}
        style={{
          maxWidth: '960px',
          margin: 'auto',
          position: 'relative',
          padding: '2rem',
          borderRadius: radius.large,
          background: `linear-gradient(135deg, ${colors.panel} 0%, ${colors.background} 100%)`,
          boxShadow: shadow.soft
        }}
      >
        <h2 style={{
          fontFamily: "ZCOOL KuaiLe, sans-serif",
          fontSize: fonts.title,
          color: colors.primary,
          marginBottom: '1rem'
        }}>
          空气质量，影响的不只是今天
        </h2>
        <p style={{
          fontFamily: "Noto Sans SC, sans-serif",
          fontSize: fonts.body,
          color: colors.textDim,
          lineHeight: '1.8',
          marginTop: '1rem'
        }}>
          -此网站整理了宁波近八年来的 PM2.5 数据-
        </p>
        <motion.svg
          initial={{ y: 0 }}
          animate={{ y: [0, 10, 0] }}
          transition={{ repeat: Infinity, duration: 2, ease: 'easeInOut' }}
          style={{ position: 'absolute', left: '50%', bottom: '-1rem', transform: 'translateX(-50%)' }}
          width="24" height="24" viewBox="0 0 24 24" fill="none"
          stroke={colors.secondary} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"
        >
          <path d="M12 5v14" />
          <path d="M19 12l-7 7-7-7" />
        </motion.svg>
      </motion.div>
    </section>
  );
};

export default HeroIntro;
