// 文件：src/components/AuthorInfo.jsx
import React from 'react';
import { motion } from 'framer-motion';

const AuthorInfo = ({ authors = ['Nick Cao', 'Peter Zhou', 'Toby Wang'] }) => {
  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 1 }}
      style={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        margin: '2rem 0'
      }}
    >
      <p
        style={{
          color: '#888',
          fontSize: '1.3rem',
          margin: 0
        }}
      >
        By {authors.join(', ')}
      </p>
      <motion.div
        initial={{ scaleX: 0 }}
        animate={{ scaleX: 1 }}
        transition={{ duration: 0.8, ease: 'easeOut' }}
        style={{
          width: '60%',
          height: '1px',
          background: '#444',
          marginTop: '0.5rem',
          transformOrigin: 'left center'
        }}
      />
    </motion.div>
  );
};

export default AuthorInfo;
