// 文件：src/components/Navbar.jsx
import React from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Tooltip,
  Legend,
  Title
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Tooltip,
  Legend,
  Title
);

const Navbar = () => {
  const getGradient = (ctx, area) => {
    const gradient = ctx.createLinearGradient(0, area.bottom, 0, area.top);
    gradient.addColorStop(0, '#8f9bb2'); // bottom
    gradient.addColorStop(1, '#cdd5e0'); // top
    return gradient;
  };

  const chartData = (canvas) => {
    const ctx = canvas.getContext('2d');
    const area = canvas.getBoundingClientRect();
    const gradient = getGradient(ctx, area);

    return {
      labels: ['1月','2月','3月','4月','5月','6月','7月','8月','9月','10月','11月','12月'],
      datasets: [
        {
          label: 'PM2.5 (μg/m³)',
          data: [35, 30, 28, 25, 20, 18, 15, 17, 22, 26, 32, 36],
          borderColor: gradient,
          backgroundColor: gradient,
          tension: 0.3,
          pointRadius: 5,
          pointHoverRadius: 8,
        },
      ],
    };
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      title: {
        display: true,
        text: '2023年月均 PM2.5 趋势',
        color: '#eee',
        font: { size: 16 },
      },
      legend: {
        labels: { color: '#ccc', font: { family: 'Noto Sans SC' } },
      },
      tooltip: {
        backgroundColor: '#222',
        titleColor: '#cdd5e0',
        bodyColor: '#eee',
        borderColor: '#cdd5e0',
        borderWidth: 1,
        displayColors: false,
        callbacks: {
          label: ctx => `PM2.5：${ctx.raw} μg/m³`,
        },
      },
    },
    scales: {
      x: { grid: { color: 'rgba(255,255,255,0.08)' }, ticks: { color: '#aaa' } },
      y: { grid: { color: 'rgba(255,255,255,0.08)' }, ticks: { color: '#aaa' } },
    },
  };

  return (
    <div style={{ height: '400px', margin: '3rem auto', maxWidth: '1000px' }}>
      <Line data={chartData} options={chartOptions} />
    </div>
  );
};

export default Navbar;
