// 文件：src/components/PM25YearWithPie.jsx
import React from 'react';
import { Line, Pie } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';

// 注册所有需要的 Chart.js 元素
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

const PM25YearWithPie = () => {
  // 月份标签
  const months = [
    '1月', '2月', '3月', '4月', '5月', '6月',
    '7月', '8月', '9月', '10月', '11月', '12月'
  ];

  // 2023 年各月 PM2.5 数据
  const pm25Data = [35, 30, 28, 25, 20, 18, 15, 17, 22, 26, 32, 36];

  // 计算四季平均值
  const winterAvg = ((pm25Data[0] + pm25Data[1] + pm25Data[11]) / 3).toFixed(1);
  const springAvg = ((pm25Data[2] + pm25Data[3] + pm25Data[4]) / 3).toFixed(1);
  const summerAvg = ((pm25Data[5] + pm25Data[6] + pm25Data[7]) / 3).toFixed(1);
  const autumnAvg = ((pm25Data[8] + pm25Data[9] + pm25Data[10]) / 3).toFixed(1);

  // 折线图数据与配置
  const lineData = {
    labels: months,
    datasets: [
      {
        label: 'PM2.5 (μg/m³)',
        data: pm25Data,
        borderColor: '#e6374e',
        backgroundColor: '#e6374e',
        tension: 0.3,
        pointRadius: 4,
        pointHoverRadius: 8
      }
    ]
  };
  const lineOptions = {
    responsive: true,
    plugins: {
      title: { display: true, text: '2023年月均 PM2.5 趋势' },
      tooltip: {
        backgroundColor: '#222',
        titleColor: '#e6374e',
        bodyColor: '#eee',
        borderColor: '#e6374e',
        borderWidth: 1,
        displayColors: false,
        callbacks: {
          label: ctx => `PM2.5：${ctx.raw} μg/m³`
        }
      },
      legend: {
        labels: { color: '#ccc', font: { size: 12 } }
      }
    },
    scales: {
      x: { ticks: { color: '#aaa' }, grid: { color: 'rgba(255,255,255,0.1)' } },
      y: { ticks: { color: '#aaa' }, grid: { color: 'rgba(255,255,255,0.1)' } }
    }
  };

  // 饼图数据与配置
  const pieData = {
    labels: ['冬季', '春季', '夏季', '秋季'],
    datasets: [
      {
        data: [winterAvg, springAvg, summerAvg, autumnAvg],
        backgroundColor: [
          'rgba(230,55,78,0.8)',
          'rgba(230,55,78,0.6)',
          'rgba(230,55,78,0.4)',
          'rgba(230,55,78,0.2)'
        ],
        borderColor: '#fff',
        borderWidth: 2
      }
    ]
  };
  const pieOptions = {
    responsive: true,
    plugins: {
      title: { display: true, text: '2023年 PM2.5 四季平均分布' },
      tooltip: {
        backgroundColor: '#222',
        bodyColor: '#eee',
        borderColor: '#e6374e',
        borderWidth: 1,
        callbacks: {
          label: ctx => `${ctx.label}：${ctx.raw} μg/m³`
        }
      },
      legend: {
        labels: { color: '#ccc', font: { size: 12 } }
      }
    }
  };

  return (
    <div
      style={{
        display: 'flex',
        flexWrap: 'wrap',
        gap: '2rem',
        justifyContent: 'center',
        padding: '2rem 0'
      }}
    >
      {/* 折线图 */}
      <div style={{ flex: '1 1 300px', maxWidth: '600px' }}>
        <Line data={lineData} options={lineOptions} />
      </div>

      {/* 饼图 */}
      <div style={{ flex: '1 1 200px', maxWidth: '400px' }}>
        <Pie data={pieData} options={pieOptions} />
      </div>
    </div>
  );
};

export default PM25YearWithPie;
