// 文件路径：src/components/PM25YearWithPie.jsx
import React from 'react';
import { Line, Pie } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  Tooltip,
  Legend,
  Title
} from 'chart.js';
import './PM25YearWithPie.css'; // 确保存在该文件，或注释掉此行防止报错

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  Tooltip,
  Legend,
  Title
);

const PM25YearWithPie = () => {
  const labels = [
    '1月', '2月', '3月', '4月', '5月', '6月',
    '7月', '8月', '9月', '10月', '11月', '12月'
  ];

  const pm25Data = {
    labels,
    datasets: [
      {
        label: 'PM2.5 月均值 (μg/m³)',
        data: [42, 35, 28, 25, 22, 18, 20, 21, 24, 30, 36, 40],
        borderColor: 'rgba(75,192,192,1)',
        backgroundColor: 'rgba(75,192,192,0.2)',
        fill: true,
        tension: 0.4,
        pointRadius: 5,
        pointHoverRadius: 7,
      }
    ]
  };

  const pieData = {
    labels: ['优（0~35）', '良（35~75）', '轻度污染（75~115）', '中度以上污染'],
    datasets: [
      {
        data: [120, 180, 45, 20], // 天数示意
        backgroundColor: ['#7dd71d', '#ffb01e', '#ff5c5c', '#a71d2a'],
        borderWidth: 1
      }
    ]
  };

  return (
    <div className="pm25-chart-wrapper">
      <h2>2024年宁波市 PM2.5 趋势图</h2>
      <div className="chart-container">
        <Line data={pm25Data} options={{ responsive: true }} />
      </div>
      <h3>全年空气质量等级占比</h3>
      <div className="chart-container">
        <Pie data={pieData} options={{ responsive: true }} />
      </div>
    </div>
  );
};

export default PM25YearWithPie;
