import React from 'react';
import { Pie } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend
} from 'chart.js';
import './PM25YearWithPie.css';

ChartJS.register(ArcElement, Tooltip, Legend);

const PM25YearWithPie = () => {
  // 2023 年每月 PM2.5 数据
  const months = ['1月','2月','3月','4月','5月','6月','7月','8月','9月','10月','11月','12月'];
  const monthlyData = [35, 30, 28, 25, 20, 18, 15, 17, 22, 26, 32, 36];

  // 计算各季度平均值
  const seasonalAverages = [
    { label: 'Q1', value: (35 + 30 + 28) / 3 },
    { label: 'Q2', value: (25 + 20 + 18) / 3 },
    { label: 'Q3', value: (15 + 17 + 22) / 3 },
    { label: 'Q4', value: (26 + 32 + 36) / 3 },
  ];
  const total = seasonalAverages.reduce((sum, s) => sum + s.value, 0);

  // 构造饼图数据（百分比）
  const pieData = {
    labels: seasonalAverages.map(s => s.label),
    datasets: [{
      data: seasonalAverages.map(s => ((s.value / total) * 100).toFixed(1)),
      backgroundColor: ['#e6374e', '#f96c86', '#aaa', '#888'],
      borderColor: '#111',
      borderWidth: 2,
    }]
  };

  const pieOptions = {
    plugins: {
      legend: {
        position: 'bottom',
        labels: { color: '#ccc', font: { size: 14 } }
      },
      tooltip: {
        backgroundColor: '#222',
        titleColor: '#e6374e',
        bodyColor: '#eee',
        borderColor: '#e6374e',
        borderWidth: 1,
        callbacks: {
          label: ctx => `${ctx.label}: ${ctx.raw}%`
        }
      }
    }
  };

  return (
    <div className="pie-table-container">
      {/* 数据表 */}
      <div className="table-wrapper">
        <table>
          <thead>
            <tr>
              <th>月份</th>
              <th>PM2.5 (μg/m³)</th>
            </tr>
          </thead>
          <tbody>
            {monthlyData.map((val, i) => (
              <tr key={i}>
                <td>{months[i]}</td>
                <td>{val}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* 饼图 */}
      <div className="pie-chart-wrapper">
        <Pie data={pieData} options={pieOptions} />
      </div>
    </div>
  );
};

export default PM25YearWithPie;
