// 文件：src/components/PM25YearWithPie.jsx

import React from 'react';
import { Line, Pie } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  Tooltip,
  Legend,
  Title
} from 'chart.js';
import './PM25YearWithPie.css';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  Tooltip,
  Legend,
  Title
);

const PM25YearWithPie = () => {
  const labels = [
    '1月','2月','3月','4月','5月','6月',
    '7月','8月','9月','10月','11月','12月'
  ];
  const dataPoints = [35,30,28,25,20,18,15,17,22,26,32,36];

  const lineData = {
    labels,
    datasets: [{
      label: 'PM2.5 (μg/m³)',
      data: dataPoints,
      borderColor: '#e6374e',
      backgroundColor: 'rgba(230,55,78,0.2)',
      tension: 0.3,
      pointRadius: 4,
      pointHoverRadius: 8
    }]
  };

  const pieData = {
    labels,
    datasets: [{
      data: dataPoints,
      backgroundColor: [
        '#ff9aa2','#ffb7b2','#ffdac1','#e2f0cb',
        '#b5ead7','#c7ceea','#f2d7d5','#f7cac9',
        '#92a8d1','#034f84','#f7786b','#b3dee2'
      ],
      borderColor: '#fff',
      borderWidth: 2
    }]
  };

  const lineOptions = {
    responsive: true,
    maintainAspectRatio: false,
    animation: { duration: 2000, easing: 'easeOutQuart' },
    plugins: {
      legend: { labels: { color: '#ccc', font: { family: 'Noto Sans SC', size: 12 } } },
      title: { display: true, text: '2023年 PM2.5 月均浓度', color: '#fff', font: { size: 16 } },
      tooltip: {
        backgroundColor: '#222',
        titleColor: '#e6374e',
        bodyColor: '#eee',
        borderColor: '#e6374e',
        borderWidth: 1,
        displayColors: false,
        callbacks: {
          label: ctx => `PM2.5：${ctx.raw} μg/m³`
        }
      }
    },
    scales: {
      x: { ticks: { color: '#aaa' }, grid: { color: 'rgba(255,255,255,0.05)' } },
      y: { ticks: { color: '#aaa' }, grid: { color: 'rgba(255,255,255,0.05)' } }
    },
    elements: {
      point: { radius: 4, hoverRadius: 8, hoverBorderWidth: 2 },
      line: { borderWidth: 2, hoverBorderWidth: 4 }
    }
  };

  const pieOptions = {
    responsive: true,
    maintainAspectRatio: false,
    animation: { duration: 1500, easing: 'easeOutQuart' },
    plugins: {
      legend: { position: 'bottom', labels: { color: '#ccc', boxWidth: 12, padding: 16 } },
      title: { display: true, text: 'PM2.5 分布（饼图）', color: '#fff', font: { size: 16 } },
      tooltip: {
        backgroundColor: '#222',
        titleColor: '#e6374e',
        bodyColor: '#eee',
        borderColor: '#e6374e',
        borderWidth: 1,
        displayColors: false,
        callbacks: {
          label: (ctx) => `${ctx.label}：${ctx.raw} μg/m³`
        }
      }
    }
  };

  return (
    <section className="pm25-year-with-pie">
      <div className="chart-container line-chart">
        <Line data={lineData} options={lineOptions} />
      </div>
      <div className="chart-container pie-chart">
        <Pie data={pieData} options={pieOptions} />
      </div>
    </section>
  );
};

export default PM25YearWithPie;
