// 文件：src/components/PM25YearWithPie.jsx
import React from 'react';
import { Line, Pie } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  Tooltip,
  Legend,
  Title,
} from 'chart.js';

// 注册需要的元素
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  Tooltip,
  Legend,
  Title
);

const PM25YearWithPie = () => {
  // 折线图数据
  const lineData = {
    labels: [
      '1月','2月','3月','4月','5月','6月',
      '7月','8月','9月','10月','11月','12月'
    ],
    datasets: [
      {
        label: 'PM2.5 (μg/m³)',
        data: [35,30,28,25,20,18,15,17,22,26,32,36],
        borderColor: '#e6374e',
        backgroundColor: '#e6374e',
        tension: 0.3,
        pointRadius: 5,
        pointHoverRadius: 8,
      },
    ],
  };

  const lineOptions = {
    responsive: true,
    maintainAspectRatio: false,  // 由容器高度控制
    plugins: {
      title: {
        display: true,
        text: '2023年月均 PM2.5 趋势',
        color: '#eee',
        font: { size: 16 },
      },
      legend: {
        labels: { color: '#ccc', font: { family: 'Noto Sans SC' } },
      },
      tooltip: {
        backgroundColor: '#222',
        titleColor: '#e6374e',
        bodyColor: '#eee',
        borderColor: '#e6374e',
        borderWidth: 1,
        displayColors: false,
        callbacks: {
          label: ctx => `PM2.5：${ctx.raw} μg/m³`,
        },
      },
    },
    scales: {
      x: { grid: { color: 'rgba(255,255,255,0.08)' }, ticks: { color: '#aaa' } },
      y: { grid: { color: 'rgba(255,255,255,0.08)' }, ticks: { color: '#aaa' } },
    },
  };

  // 饼图数据（四季分布示例）
  const pieData = {
    labels: ['冬季', '春季', '夏季', '秋季'],
    datasets: [
      {
        data: [33, 22, 15, 30], // 示例：冬33% 春22% 夏15% 秋30%
        backgroundColor: [
          'rgba(230,55,78,0.8)',
          'rgba(230,55,78,0.6)',
          'rgba(230,55,78,0.4)',
          'rgba(230,55,78,0.9)',
        ],
        borderColor: '#fff',
        borderWidth: 2,
      },
    ],
  };

  const pieOptions = {
    responsive: true,
    maintainAspectRatio: false,  // 由容器高度控制
    plugins: {
      title: {
        display: true,
        text: '2023年 PM2.5 四季平均分布',
        color: '#eee',
        font: { size: 16 },
      },
      legend: {
        position: 'bottom',
        labels: { color: '#ccc', font: { family: 'Noto Sans SC' } },
      },
      tooltip: {
        backgroundColor: '#222',
        bodyColor: '#eee',
        borderColor: '#fff',
        borderWidth: 1,
        callbacks: {
          label: ctx => `${ctx.label}：${ctx.raw}%`,
        },
      },
    },
  };

  return (
    <div
      style={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        gap: '2rem',
        margin: '4rem 0',
        flexWrap: 'wrap',
      }}
    >
      {/* 折线图容器 */}
      <div
        style={{
          flex: '1 1 60%',
          minWidth: '300px',
          height: '400px',
        }}
      >
        <Line data={lineData} options={lineOptions} />
      </div>

      {/* 饼图容器 */}
      <div
        style={{
          flex: '0 1 35%',
          minWidth: '250px',
          height: '400px',
        }}
      >
        <Pie data={pieData} options={pieOptions} />
      </div>
    </div>
  );
};

export default PM25YearWithPie;
