// 文件：src/components/AuthorInfo.jsx
import React from 'react';
import { motion } from 'framer-motion';

const AuthorInfo = ({
  authors = ['Nick Cao', 'Peter Zhou', 'Toby Wang']
}) => {
  // 拼接为 "N I C K   C A O" 这种样式
  const styledText = authors.join(' · ').toUpperCase().split('').map((char, i) => (
    <motion.span
      key={i}
      initial={{ opacity: 0, y: 10 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ delay: i * 0.05 }}
      style={{
        display: 'inline-block',
        marginRight: char === ' ' ? '0.5rem' : '0.2rem',
        color: '#e6374e',
        fontWeight: 500,
        fontSize: '1.3rem',
        letterSpacing: '2px',
      }}
    >
      {char}
    </motion.span>
  ));

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      whileInView={{ opacity: 1, y: 0 }}
      transition={{ duration: 1 }}
      viewport={{ once: true }}
      style={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        margin: '3rem 0',
      }}
    >
      <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
        {styledText}
      </div>

      {/* 炫酷下划线动画 */}
      <motion.div
        initial={{ scaleX: 0 }}
        animate={{ scaleX: 1 }}
        transition={{ duration: 1, ease: 'easeOut', delay: 0.6 }}
        style={{
          width: '70%',
          height: '2px',
          background: 'linear-gradient(to right, #e6374e, #ff8a65)',
          marginTop: '0.8rem',
          transformOrigin: 'left center',
          borderRadius: '999px',
        }}
      />
    </motion.div>
  );
};

export default AuthorInfo;
